--
-- Table structure for table `buku`
--

CREATE TABLE IF NOT EXISTS `buku` (
  `id_buku` int(4) NOT NULL AUTO_INCREMENT,
  `judul_buku` varchar(100) NOT NULL,
  `desc_buku` text NOT NULL,
  `penerbit` varchar(50) NOT NULL,
  `gambar` varchar(20) NOT NULL,
  PRIMARY KEY (`id_buku`)
) ENGINE=InnoDB  DEFAULT CHARSET=latin1 AUTO_INCREMENT=11 ;

--
-- Dumping data for table `buku`
--

INSERT INTO `buku` (`id_buku`, `judul_buku`, `desc_buku`, `penerbit`, `gambar`) VALUES
(1, 'Anda Ingin Sukses?', '<p>\r\n<i>Video book</i>\r\nini berisi tentang rahasia kesuksesan yang telah menghantarkan seorang pemuda, Julianto Eka Putra, dari seorang\r\n<i>marketing</i>\r\ndi sebuah bank swasta menjadi seorang milyarder muda. Pengetahuan dan pengalaman sebagai seorang pengusaha yang berhasil juga telah dibagikan lewat seminar-seminar yang telah dihadiri oleh ratusan ribu orang berbagai kota besar di Indonesia. Dalam\r\n<i>video book</i>\r\nini Julianto Eka Putra selalu menekankan untuk mempersiapkan diri kita dengan\r\n<b>PAKSA</b>\r\n, yaitu: \r\n<b>P</b>\r\nray, \r\n<b>A</b>\r\nttitude, \r\n<b>K</b>\r\nnowledge, \r\n<b>S</b>\r\nkill, \r\n<b>A</b>\r\nction.\r\n</p>\r\n<p>\r\nSaat ini “Rahasia Kesuksesan” tersebut dihadirkan dalam bentuk yang unik yaitu\r\n<i>video book</i>\r\n. \r\n<i>Video Book</i>\r\nini terdiri dari buku dan dvd yang berisikan tentang tuntunan untuk sukses yang sangat mudah untuk diaplikasikan dalam kehidupan kita sehari-hari. Selain itu,\r\n<i>video book</i>\r\nini juga dilengkapi dengan kisah sukses dari orang-orang besar yang kisah hidupnya telah menggugah dunia, seperti Walt Disney, Beethoven, Thomas Alva Edison, Abraham Lincoln, Jackie Chan, dan lain-lain.\r\n</p>\r\n<p>Selamat menemukan Rahasia Sukses tersebut.</p>', 'mic Publishing', 'buku_1456892345.png'),
(2, 'The Secret of Wealth Creation', '<p>\r\nYang harus Anda lakukan adalah\r\n<b>Ciptakan Kekayaan Anda. </b>\r\nPerlu Anda ketahui, banyak orang ingin melakukan hal ini, tetapi tidak banyak yang tahu cara tepat untuk melakukannya. Untuk itu, Anda perlu membaca\r\n<i>The Secret of Wealth Creation. </i>\r\nMengapa?\r\n</p>\r\n<p>Sebab konsep yang dikemukakan dalam buku ini benar-benar akan membuka pikiran Anda dan membuat Anda menyadari bahwa sesungguhnya menjadi seorang pegusaha itu mudah. Salah satunya, cukup dengan memilih kategori modal mana yang akan Anda gunakan. Modal itu berupa:</p>\r\n<p>\r\n–          1M (\r\n<i>Me</i>\r\n, atau diri Anda sendiri)\r\n</p>\r\n<p>\r\n–          2M (\r\n<i>Me+Material</i>\r\n)\r\n</p>\r\n<p>\r\n–          3M (\r\n<i>Me+Material+Management</i>\r\n)\r\n</p>\r\n<p>Ingatlah bahwa menciptakan kekayaan tidak harus diawali dengan modal yang besar, tetapi cukup Anda sebagai pribadi berkemauan yang besar. Menciptakan kekayaan tidak melulu soal angka, tetapi tentang seberapa bahagia Anda terhadap pencapaian Anda.</p>', 'MIC Publishing', 'buku_1456892378.jpg'),
(3, 'Orang Orang Berdosa Ingin Berkarya', '<p>Buku ini disusun berdasarkan kisah nyata dari dua orang yang berhasil membangun hidup dan bangkit dari kesalahan dan dosa masa lalunya, Aa Gym dan Julianto Eka Putra, dua paduan yang berbeda dari dua sisi kehidupan yang berbeda pula. Buku ini membagikan pandangan baru mengenai bekerja untuk menciptakan suatu karya, yaitu sebagai anugerah dari Allah, serta untuk menjalankan amanat perintah-Nya dan juga amanat dari orang tua, keluarga, serta bangsa dan negara kita. Dengan kerja keras dan tekad ', 'mic Publishing', 'buku_1456892742.jpg'),
(4, 'Becoming Enterpreuneur is Easy', '<p>Seorang penyair pernah menuliskan,</p>\r\n<p>Hidup saya bisa menyentuh banyak kehidupan lainnya, sebelum hari ini berlalu;</p>\r\n<p>Meninggalkan jejak-jejak yang tak terhitung akan kebaikan atau rasa sakit, menjelang datangnya mentari senja;</p>\r\n<p>Inilah harapan yang selalu kubisikkan, doa yang selalu kupanjatkan;</p>\r\n<p>Ya Tuhan, biarlah hidupku menolong kehidupan lain yang disentuhnya di sepanjang jalan.</p>\r\n<p>Tentu inilah kerinduan hati setiap kita, pemimpin atau calon pemimpin yang ingin menjalani hidup penuh makna dan bermanfaat bagi sesama. Bahkan, John C. Maxwell dan Jim Dornan melangkah lebih jauh lagi saat berkata,“Jika Anda sangat ingin sukses atau berdampak positif terhadap dunia, jadilah pribadi yang berpengaruh. Tanpa pengaruh, tidak ada kesuksesan.”</p>\r\n<p>\r\nBerita baiknya, setiap orang pasti memengaruhi orang lain. Namun bagaimana Anda bisa memastikan pengaruh itu mengubah hidup orang lain – dan tentu hidup Anda sendiri – menjadi lebih baik?\r\n<br>\r\nDengan penuturan yang gamblang, menghibur, dan jauh dari kesan menggurui,\r\n<i>Becoming a Person of Influence</i>\r\n, karya\r\n<i> </i>\r\nJohn C. Maxwell dan Jim Dornan, akan menuntun Anda menjadi pribadi yang berpengaruh positif. Buku sarat pengalaman dan inspirasi ini menguraikan\r\n<b>10 M </b>\r\nyang biasanya dilakukan Pribadi Berpengaruh saat berelasi dengan orang lain, antara lain:\r\n</p>\r\n<ol>\r\n<li>\r\n<b>M</b>\r\nenjaga integritas\r\n</li>\r\n<li>\r\n<b>M</b>\r\nembimbing\r\n</li>\r\n<li>\r\n<b>M</b>\r\nenanamkan keyakinan\r\n</li>\r\n<li>\r\n<b>M</b>\r\nendengarkan\r\n</li>\r\n<li>\r\n<b>M</b>\r\nemahami\r\n</li>\r\n<li>\r\n<b>M</b>\r\nengembangkan\r\n</li>\r\n<li>\r\n<b>M</b>\r\nengarahkan\r\n</li>\r\n<li>\r\n<b>M</b>\r\nenjalin hubungan\r\n</li>\r\n<li>\r\n<b>M</b>\r\nemampukan\r\n</li>\r\n<li>\r\n<b>M</b>\r\nencetak pribadi berpengaruh lainnya\r\n</li>\r\n</ol>', 'mic Publishing', 'buku_1456994090.png'),
(5, 'Selamat Pagi Indonesia', '<p>SEPERTI disambar petir di siang bolong, pialaku langsung jatuh begitu saja dan pecah. Saat itu aku bukannya merasa sedih, tetapi justru ingin marah pada ayah. Aku sudah berjuang keras untuk lomba dan akhirnya menang. Namun saat hendak menunjukkan piala tersebut sebagai bukti, kenyataan yang kuhadapi justru ayah pergi selamanya.</p>\r\n\r\n<p>Petikan alinea tersebut, sekilas seperti isi sebuah cerpen. Namun cerita itu merupakan sebuah kisah nyata salah satu siswa SMA Selamat Pagi Indonesia, berlokasi di kawasan Kampoeng Kidz Jalan Dieng Pandanrejo Kota Batu. Kisah nyata anak-anak SMA Selamat Pagi itu, kemudian dituangkan dalam sebuah buku.</p>\r\n\r\n<p>\r\n<br>\r\nDalam cetakan perdana itu, kata dia, buku tersebut mendapatkan sambutan hangat dari pembaca baik yang mendapatkan dari toko buku ataupun saat  datang ke Kampoeng Kidz. Kini Kampoeng Kidz, sudah berancang-ancang menerbitkan cetakan kedua dengan jumlah lebih besar.  ‘’Kami sangat bangga Pak Menteri BUMN bisa berkunjung ke sini. Kedatangan Beliau ke sini, menjadi tambahan semangat kami untuk berjuang semakin maju,’’ tegas Agus.\r\n<br>\r\nBuku Selamat Pagi Indonesia, lebih banyak berisi testimoni para siswa. Mereka adalah siswa dalam kondisi perekonomian keluarga, yang sangat memprihatinkan. Sedangkan Yayasan Selamat Pagi Indonesia, memberikan mereka bersekolah secara gratis.\r\n</p>\r\n<p>\r\n<br>\r\nSiswa yang meraih juara dan mendapatkan ayah meninggal saat hendak menyerahkan piala, adalah Sheren Della Sandra asal Madiun. Ketika itu, ayahnya menjanjikan bakal membelikan sepeda mini bila Sheren juara lomba. Namun ayahnya keburu meninggal sebelum membelikan sepeda mini.\r\n<br>\r\nDalam kisahnya, keluarganya sangat miskin setelah sang ayah wafat. Siswa tersebut memiliki tiga orang saudara, dan ibunya sendiri tidak bekerja. Untuk membeli beras saja, keluarga Sheren tidak mampu sehingga beberapa kali mendapatkan sumbangan dari orang lain.\r\n</p>\r\n<p>\r\n<br>\r\nBegitu pula untuk membayar SPP, keluarga ini juga tidak mampu sehingga Sheren memilih sekolah di SMA Selamat Pagi Indonesia Kota Batu. ‘’Semua yang ditulis di buku ini, merupakan kisah nyata,’’ pungkasnya.\r\n<br>\r\nMenurutnya, para siswa selama ini tinggal di asrama sekolah ketika belajar di SMA Selamat Pagi Indonesia. Kampoeng Kidz, merupakan salah satu sarana mendapatkan uang karena adanya kunjungan dari berbagai wisatawan maupun siswa-siswa yang menikmati oudbond.(feb/lyo)\r\n</p>', 'mic publishing', 'buku_1457581833.jpg'),
(6, 'Secret Book of MLM', '<p>Pandangan yang salah tentang bisnis jaringan atau multi-level marketing, sering menyebabkan banyak orang berpandangan negatif sebelum mereka menyelidiki dan membuktikan sendiri strategi pemasaran dengan sistem multi-level marketing. Sementara itu, banyak orang telah mencoba untuk menjalankan bisnis ini tanpa dukungan pengetahuan dan panduan sistem yang benar. Akibatnya, keberhasilan merekapun tertunda. Bagaimanakah cara melakukan presentasi, follow-up, dan mensponsori orang sebagai sikap yang akan mendukung kesuksesan dalam bisnis jaringan? Bagaimanakah membuat rencana kerja harian? Apa elemen-elemen lain yang dibutuhkan untuk mencapai kesuksesan tertinggi dalam profesi ini?</p>\r\n<p>Semua jawaban pertanyaan Anda akan Anda temukan dalam buku ini. Ditulis oleh para praktisi di bidang multi-level marketing yang mengetahui seluk-beluk bisnis ini hingga ke akarnya. Untuk membantu Anda meraih kesuksesan dan mempercayai setiap kegagalan yang menghadang Anda sebagai langkah awal menuju puncak karier Anda. Buku ini terdiri dari 31 rahasia yang mengupas arti sebenarnya dari multi-level marketing. Lebih lagi, setiap langkah merupakan pengalaman nyata orang-orang yang telah sukses menjalankan bisnis ini.</p>\r\n<p>Langkah demi langkah dalam buku ini siap menuntun Anda untuk menjelajahi setiap rahasia kesuksesan di dunia multi-level marketing. This What a Book Should Do!!</p>', 'mic publishing', 'buku_1457582175.jpg'),
(7, 'Secret of wealth Management', '<p>Anda sudah bekerja keras. 18 jam satu hari. 7 hari satu minggu. Namun Anda hanya menghasilkan sedikit uang. Sangat disayangkan bahwa uang yang Anda hasilkan pun ternyata habis begitu saja … seakan-akan kerja keras Anda lenyap tak berbekas.</p>\r\n<p>Apa yang Anda butuhkan?</p>\r\n<p>Pengelolaan Kekayaan.</p>\r\n<p>Menurut Wealth Manager Association, Pengelolaan Kekayaan diawali dengan Wealth Creation, menciptakan kekayaan itu sendiri – yang paling baik jika diawali dengan cara menambah penghasilan. Hanya ada satu hal yang perlu diingat: jangan takut gagal!</p>\r\n<p>Jika Anda memiliki penghasilan, Anda harus mengelola kekayaan Anda itu, berapapun jumlahnya. Justru di saat penghasilan Anda tidak berlebihan inilah Anda harus benar-benar mengelolanya agar bisa memenuhi semua kebutuhan dan keinginan Anda serta memiliki dana untuk berjaga-jaga.</p>\r\n<p>Setelah berhasil mengalokasikan kekayaan Anda dengan baik, Anda juga disarankan untuk melindungi kekayaan Anda itu. Anda bisa melakukan proteksi ini sendiri, dengan cara menyiapkan tabungan khusus, atau menyerahkannya pada pihak asuransi.</p>\r\n<p>Tahap terakhir dari pengelolaan kekayaan adalah pemindahan kekayaan – kepada ahli waris Anda, baik setelah atau sebelum Anda meninggal dunia.</p>\r\n<p>Selama bertahun-tahun, para anggota Weatlh manager Association telah mempraktikkan langkah-langkah pengelolaan kekayaan ini dan  membantu banyak orang lainnya memperoleh keberhasilan yang serupa. Dengan cara menekankan pada prosesnya, bukan hasilnya, The Secret of Wealth Management tidak hanya menawarkan panduan, tetapi rumusan pasti yang akan menjamin kesuksesan Anda.</p>', 'MIC', 'buku_1457582529.png'),
(8, 'I Am Legend', '<p>Diangkat dari kisah nyata siswa-siswi SMA Selamat Pagi Indonesia demi meraih VALUABLE LIFE. SMA Selamat Pagi Indonesia terletak di Batu, Malang. Sekolah ini adalah sekolah gratis yang dikhususkan untuk anak-anak yatim piatu atau miskin, dipilih dari 5 agama dan berbagai penjuru Indonesia, mulai Sabang sampai Merauke. Di SMA Selamat Pagi Indonesia, tiap siswa menerapkan PAKSA (Pray, Attitude, Knowledge, Skill, Action), experiential learning sampai ke luar negeri, menjadi VALUABLE PERSON, dan memanfaatkan 5 POWERS dari Tuhan dengan sebaik-baiknya. Siswa-siswi SMA Selamat Pagi Indonesia yang dulunya dianggap tidak bermasa depan pun bertransformasi menjadi manusia-manusia bernilai dan bermanfaat bagi sesama.</p>', 'mic publishing', 'buku_1457582812.jpg'),
(9, 'My Transformation', '<p>Diangkat dari kisah nyata siswa-siswi SMA Selamat Pagi Indonesia demi meraih VALUABLE LIFE. SMA Selamat Pagi Indonesia terletak di Batu, Malang. Sekolah ini adalah sekolah gratis yang dikhususkan untuk anak-anak yatim piatu atau miskin, dipilih dari 5 agama dan berbagai penjuru Indonesia, mulai Sabang sampai Merauke. Di SMA Selamat Pagi Indonesia, tiap siswa menerapkan PAKSA (Pray, Attitude, Knowledge, Skill, Action), experiential learning sampai ke luar negeri, menjadi VALUABLE PERSON, dan memanfaatkan 5 POWERS dari Tuhan dengan sebaik-baiknya. Siswa-siswi SMA Selamat Pagi Indonesia yang dulunya dianggap tidak bermasa depan pun bertransformasi menjadi manusia-manusia bernilai dan bermanfaat bagi sesama.</p>', 'mic publishing', 'buku_1457585948.jpg'),
(10, 'Today Is Tommorow', '<p>Diangkat dari kisah nyata siswa-siswi SMA Selamat Pagi Indonesia demi meraih VALUABLE LIFE. SMA Selamat Pagi Indonesia terletak di Batu, Malang. Sekolah ini adalah sekolah gratis yang dikhususkan untuk anak-anak yatim piatu atau miskin, dipilih dari 5 agama dan berbagai penjuru Indonesia, mulai Sabang sampai Merauke. Di SMA Selamat Pagi Indonesia, tiap siswa menerapkan PAKSA (Pray, Attitude, Knowledge, Skill, Action), experiential learning sampai ke luar negeri, menjadi VALUABLE PERSON, dan memanfaatkan 5 POWERS dari Tuhan dengan sebaik-baiknya. Siswa-siswi SMA Selamat Pagi Indonesia yang dulunya dianggap tidak bermasa depan pun bertransformasi menjadi manusia-manusia bernilai dan bermanfaat bagi sesama.</p>\r\n', 'mic publishing', 'buku_1457586217.jpg');

